import React, { ReactElement, useState } from 'react'

interface Props {

}

export default function Distributionofgames({}: Props): ReactElement {
    const [email, setEmail] = useState('');
    const [emailDirty, setEmailDirty] = useState(false);
    const [emailError, setEmailError] = useState('Емейл не может быть пустым');


    const blurHandler = (e: React.FocusEvent<HTMLInputElement>) => {
        switch (e.target.name){
            case 'email':
                setEmailDirty(true)
                break
        }
    }



    return (
    <div className='distributionofgames'>
        <form>
           <h1>Подписка</h1>
           {(emailDirty && emailError) && <div style={{color:'red'}}>{emailError}</div>}
           <input valonBlur={e => blurHandler(e)} name='email' type='text' placeholder='Enter your email.....' />
           <button type='submit'>Участвовать</button>
       </form>
   </div>
    )
}
