import React, { Props, ReactElement, useEffect, useState } from 'react'
import UpdatedComponent from "../requiremetns_component/HocForms";


interface Props {

}


function Distributionofgames(this: any, {}: Props): ReactElement {
    const {email, setEmail} = this.props
    const {emailDirty, setEmailDirty} = this.props
    const {emailError, setEmailError} = this.props
    const {formValid, setFormValid} = this.props
    const {emailHandler} = this.props
    const {blurHandler} = this.props

    let count: number;
    count = 0;


    return (
    <div className='distributionofgames'>
        <form>
           <h1>Подписка</h1>
           {(emailDirty && emailError) && <div style={{color:'red'}}>{emailError}</div>}
           <input onChange={e => emailHandler(e)} value = {email} onBlur={e => blurHandler(e)} name='email' type='text' placeholder='Enter your email.....' />
           <button disabled={!formValid} type='submit'>Участвовать</button>
       </form>
       <label>{count} участников.</label>
   </div>
    )
}

export default UpdatedComponent(Distributionofgames)
