interface Props {

}
interface State {
    emailList: any;
}

export default class ormtest extends Component<Props, State> {

    constructor(props: Props) {
        super(props);
        this.state = {
          emailList: [],
        };

        const numberOfmember = emailList.length
      }


    handleEmailChange = (event: { target: { value: any; }; }) => {
        this.setState({
            emailList: event.target.value
        })
    }

    handleSubmit = (event: { preventDefault: () => void; }) => {
        alert(`${this.state.emailList}`)
        event.preventDefault()
    }

    render() {
        return (
            <form onSubmit = {this.handleSubmit}>
                <div>
                    <label>Please input your email</label>
                    <input type="text" onChange={this.handleEmailChange} />
                </div>
                <button type = "submit">Submit</button>
                <p>number of participation = {}</p>
            </form>
        )
    }
}
